INSERT INTO `product` (`id`, `catalog_id`, `name`, `content`, `price`, `image_link`, `trailer_link`, `author_id`, `sale`, `status`, `number_user`, `list_number`, `content_full`) VALUES
(39, 63, 'Bí quyết viết CV - Dự phỏng vấn', 'Bạn sẽ nắm toàn bộ cách viết CV ấn tượng - Dự phỏng vấn tự tin - Chinh phục nhà tuyển dụng thành công', 17.00, '[fHW]-sddefault-(1).webp', '[oKg]-Kỹ-năng-viết-CV,-dự-phỏng-vấn,-chinh-phục-nhà-tuyển-dụng---Hán-Quang-Dự.mp4', 19, 5, 1, 2, '[19,44]', '<h3>Bạn sẽ học được g&igrave;</h3>\n<div class=\"content\">\n<div class=\"row\">\n<div class=\"col-sm-6 form-group\">\n<div class=\"title\">Kh&aacute;m ph&aacute; điểm mạnh, điểm yếu của bản th&acirc;n m&igrave;nh</div>\n</div>\n<div class=\"col-sm-6 form-group\">\n<div class=\"title\">Định hướng ph&aacute;t triển nghề nghiệp cho tương lai của bản th&acirc;n</div>\n</div>\n<div class=\"col-sm-6 form-group\">\n<div class=\"title\">C&aacute;ch viết đơn xin việc đơn giản m&agrave; ấn tượng, hấp dẫn</div>\n</div>\n<div class=\"col-sm-6 form-group\">\n<div class=\"title\">C&aacute;ch viết CV xin việc th&ocirc;i mi&ecirc;n v&agrave; chinh phục nh&agrave; tuyển dụng</div>\n</div>\n<div class=\"col-sm-6 form-group\">\n<div class=\"title\">C&aacute;c bước để chinh phục nh&agrave; tuyển dụng tại buổi phỏng vấn</div>\n</div>\n<div class=\"col-sm-6 form-group\">\n<div class=\"title\">C&aacute;ch vận động h&agrave;nh lang để vượt qua v&ograve;ng hồ sơ một c&aacute;ch nhanh nhất</div>\n</div>\n<div class=\"col-sm-6 form-group\">\n<div class=\"title\">Hướng dẫn c&aacute;ch t&igrave;m những doanh nghiệp ph&ugrave; hợp với định hướng nghề nghiệp</div>\n</div>\n<div class=\"col-sm-6 form-group\">\n<div class=\"title\">Lộ tr&igrave;nh để từ một nh&acirc;n vi&ecirc;n thử việc th&agrave;nh một nh&acirc;n vi&ecirc;n b&igrave;nh thường v&agrave; th&agrave;nh một nh&acirc;n sự</div>\n<div class=\"title\">\n<p><strong>C&oacute; phải bạn l&agrave;:</strong></p>\n<ul>\n<li>Sinh vi&ecirc;n đại học&nbsp;đang cần đi t&igrave;m việc l&agrave;m hoặc sinh vi&ecirc;n mới tốt nghiệp ra trường?&nbsp;</li>\n<li>Bạn đang t&igrave;m kiếm những mẫu CV ấn tượng để bổ sung v&agrave;o bộ hồ sơ xin việc của m&igrave;nh?</li>\n<li>Bạn đ&atilde; nộp CV nhiều nơi m&agrave; kh&ocirc;ng được gọi đi phỏng vấn? Phải chăng CV của bạn c&oacute; vấn đề, kh&ocirc;ng tạo được ấn tượng với nh&agrave; tuyển dụng?</li>\n<li>Bạn đ&atilde; phỏng vấn nhiều nơi nhưng đều thất bại? Hoặc d&ugrave; đ&atilde; cố gắng nhưng bạn vẫn kh&ocirc;ng qua khỏi v&ograve;ng thử việc d&ugrave; khả năng ứng xử giao tiếp của bạn tốt?&nbsp;</li>\n<li>Bạn cần một sự thay đổi thật t&iacute;ch cực, bắt đầu ngay từ CV xin việc của bạn nhưng lại kh&ocirc;ng biết l&agrave;m thế n&agrave;o để c&oacute; thể c&oacute; được một CV dự phỏng vấn thật ấn tượng v&agrave; thu h&uacute;t nh&agrave; tuyển dụng?</li>\n</ul>\n<p>Thấu hiểu những kh&oacute; khăn tr&ecirc;n&nbsp;của bạn, nhất l&agrave; đối với những bạn đang muốn ứng tuyển v&agrave;o c&aacute;c c&ocirc;ng ty doanh nghiệp lớn với sự cạnh tranh cao, Unica đ&atilde; rất vinh hạnh kết hợp c&ugrave;ng Giảng vi&ecirc;n H&aacute;n Quang Dự cho ra mắt&nbsp;kh&oacute;a học&nbsp;trực tuyến \"<strong>B&iacute; quyết viết CV - Dự phỏng vấn\"</strong>, chinh phục nh&agrave; tuyển dụng để gi&uacute;p bạn chinh phục được nh&agrave; tuyển dụng, vượt qua mọi kh&oacute; khăn tr&ecirc;n con đường Ứng tuyển việc l&agrave;m.&nbsp;</p>\n<p><strong>Kh&oacute;a học c&oacute; g&igrave; d&agrave;nh cho bạn?</strong></p>\n<p>Với&nbsp;thời lượng 2 giờ 29 ph&uacute;t được ph&acirc;n chia th&agrave;nh 6 phần nội dung với những chủ đề kh&aacute;c nhau như Kiến thức nền tảng, c&aacute;ch để tạo một CV xin việc ấn tượng, c&aacute;ch để tạo ấn tượng tốt với nh&agrave; tuyển dụng... kh&oacute;a học sẽ hướng dẫn bạn c&aacute;ch l&agrave;m thế n&agrave;o để c&oacute; thể chinh phục được nh&agrave; tuyển dụng ngay từ c&aacute;i nh&igrave;n đầu ti&ecirc;n.</p>\n<p>Đặc biệt, đ&acirc;y kh&ocirc;ng đơn giản l&agrave; một kh&oacute;a học ph&aacute;t triển bản th&acirc;n, m&agrave; l&agrave; một kh&oacute;a huấn luyện thực thụ, bởi chỉ cần tham gia, chắc chắn bạn sẽ c&oacute; được mẫu CV xin việc thật ẤN TƯỢNG v&agrave; ho&agrave;n hảo để CHINH PHỤC được&nbsp;nh&agrave; tuyển dụng. K&egrave;m theo đ&oacute; l&agrave; 1 LỘ TR&Igrave;NH HO&Agrave;N HẢO từ khi xin việc, vượt qua thời gian thử việc, trở th&agrave;nh nh&acirc;n vi&ecirc;n ch&iacute;nh thức v&agrave; l&agrave; nh&acirc;n vi&ecirc;n cốt c&aacute;n của c&ocirc;ng ty.</p>\n<p>Hơn thế nữa, sau kh&oacute;a học n&agrave;y, bạn c&oacute; thể gi&uacute;p được rất nhiều người kh&aacute;c cũng c&oacute; được mẫu đơn xin việc v&agrave; trao lại những kỹ năng để đạt được v&agrave; giữ lấy c&ocirc;ng việc m&igrave;nh mong muốn.</p>\n</div>\n</div>\n</div>\n</div>'),
(40, 67, 'Bí quyết kiếm Tiền trên Youtube bằng Video Animation: \"Hiệu ứng tay vẽ\"', 'Kiếm Tiền Trên Youtube từ Video Animation.', 5.00, '[eyc]-maxresdefault.jpg', '[Cp7]-Bí-quyết-kiếm-tiền-trên-Youtube-bằng-Video-Animation.mp4', 19, 0, 1, 1, '[45]', '<p>Bạn sẽ học được g&igrave;:</p>\n<p>Bạn sẽ l&agrave;m được video Animation chuy&ecirc;n nghiệp v&agrave; cuốn h&uacute;t người xem.</p>\n<p>Biết c&aacute;ch x&acirc;y dựng &yacute; tưởng để c&oacute; thể tr&igrave;nh b&agrave;y một nội dung l&ocirc;i cuốn v&agrave; hấp dẫn.</p>\n<p>Biết c&aacute;ch x&acirc;y dựng nội dung để c&oacute; thể kiếm thu nhập thụ động tr&ecirc;n youtube từ con số 0.</p>\n<p>Tự m&igrave;nh l&agrave;m được video chỉ trong 2-3h, gi&uacute;p bạn tiết kiệm được thời gian v&agrave; chi ph&iacute;.</p>\n<p>Thu h&uacute;t được nhiều kh&aacute;ch h&agrave;ng tiềm năng, từ đ&oacute; gia tăng nguồn t&agrave;i ch&iacute;nh.</p>\n<p>Được học v&agrave; thực h&agrave;nh 5 phần mềm: Photoshop, illustrator, adobe audition, camtasia</p>\n<p>Bạn c&oacute; biết rằng ng&agrave;y nay Video Marketing đang l&agrave; xu hướng để ch&uacute;ng ta c&oacute; thể tiếp cận với kh&aacute;ch h&agrave;ng một c&aacute;ch dễ d&agrave;ng v&agrave; hiệu quả nhất. V&agrave; trong những c&aacute;ch l&agrave;m video marketing hiện nay, Animation đang l&agrave; c&aacute;ch hiệu quả m&agrave; rất nhiều c&aacute;c k&ecirc;nh youtube lớn đang &aacute;p dụng như Web5ngay, B&agrave;i Học Th&agrave;nh C&ocirc;ng, Kiến Thức Th&uacute; Vị,... hay ch&iacute;nh k&ecirc;nh của m&igrave;nh KNSV TV cũng đang đi theo xu hướng đ&oacute;.</p>\n<p>Đ&acirc;y sẽ l&agrave; kh&oacute;a học m&agrave; c&aacute;c bạn sẽ được học 5 phần mềm đồ họa để c&oacute; thể tạo ra được những video animation chuy&ecirc;n nghiệp, đ&oacute; ch&iacute;nh l&agrave;: Photoshop, illustrator, adobe audition, camtasia, v&agrave; 1 phần mềm đặc biệt để tạo video animation m&agrave; m&igrave;nh sẽ dạy trong kh&oacute;a học n&agrave;y. Kh&oacute;a học l&agrave; sự chia sẻ tất cả c&aacute;c kinh nghiệm từ việc t&igrave;m v&agrave; x&acirc;y dựng &yacute; tưởng, quy tr&igrave;nh ho&agrave;n thiện để bắt đầu l&agrave;m 1 k&ecirc;nh Youtube từ con số 0.</p>\n<p>V&agrave; đặc biệt xuy&ecirc;n suốt qu&aacute; tr&igrave;nh học bi&ecirc;n tập video bạn sẽ lu&ocirc;n được thực h&agrave;nh để c&oacute; được kỹ năng l&agrave;m video chuy&ecirc;n nghiệp. Bạn chỉ cần c&oacute; 1 chiếc Laptop hoặc Pc c&oacute; cấu h&igrave;nh ổn v&agrave; kết nối Internet l&agrave; ch&uacute;ng ta đ&atilde; c&oacute; thể bắt đầu.</p>\n<p>Nhanh ch&oacute;ng đăng k&yacute; kh&oacute;a học tại Unica.vn để kiếm tiền từ video animation chuy&ecirc;n nghiệp.</p>'),
(41, 76, 'Học giỏi Tiếng Anh toàn diện: Nghe, Nói, Đọc, Viết', 'Học giỏi Tiếng Anh toàn diện giúp bạn thành thạo 4 kỹ năng quan trọng nhất: Nghe, Nói, Đọc, Viết.', 23.00, '[eMR]-hoc-gioi-tieng-anh-toan-dien_m_1555572380.jpg', '[xjZ]-HỌC-GIỎI-TIẾNG-ANH-TOÀN-DIỆN_-NGHE,-NÓI,-ĐỌC,-VIẾT.mp4', 19, 0, 1, 1, '[47]', '<p dir=\"ltr\">X&atilde; hội ng&agrave;y c&agrave;ng ph&aacute;t triển, Tiếng Anh trở th&agrave;nh một ng&ocirc;n ngữ mang t&iacute;nh chất to&agrave;n cầu m&agrave; bất cứ ai cũng muốn sở hữu n&oacute;. Giao tiếp tiếng Anh tốt kh&ocirc;ng chỉ gi&uacute;p bạn kh&aacute;m ph&aacute; nền văn h&oacute;a của nhiều nước thế giới, l&agrave; cơ hội để học hỏi v&agrave; hội nhập m&agrave; n&oacute; c&ograve;n l&agrave; một &ldquo;vũ kh&iacute; lợi hại&rdquo; để bạn mở rộng cơ hội việc l&agrave;m với mức lương cao. Để l&agrave;m được điều n&agrave;y, bạn cần nắm chắc 4 kỹ năng: Nghe, n&oacute;i, đọc, viết trong Tiếng Anh.&nbsp;</p>\n<p dir=\"ltr\"><em><strong>Thực tế chỉ ra rằng,</strong></em></p>\n<p dir=\"ltr\">Tiếng Anh c&oacute; khoảng 750.000 từ vựng. Nhưng thực ra, nếu bạn nắm được 3000 từ th&ocirc;ng dụng nhất, bạn c&oacute; thể th&agrave;nh thạo 4 kỹ năng tiếng anh m&agrave; nhiều người hằng mong đợi.&nbsp;</p>\n<p dir=\"ltr\"><em><strong>Tuy nhi&ecirc;n,&nbsp;</strong></em></p>\n<p dir=\"ltr\">- Đối với những người học tiếng Anh online, một trong những điều kh&oacute; khăn nan giải nhất ch&iacute;nh l&agrave; học từ vựng v&agrave; thực h&agrave;nh ch&uacute;ng thế n&agrave;o để đạt được hiệu quả tối ưu nhất ?</p>\n<p dir=\"ltr\">- Bạn đang ph&aacute;t triển vốn từ bằng c&aacute;ch cố gắng tiếp thu c&agrave;ng nhiều từ một ng&agrave;y c&agrave;ng tốt?</p>\n<p dir=\"ltr\">- Bạn đang học từ vựng bằng c&aacute;ch nhẩm đi nhẩm lại để nhớ kĩ hơn?</p>\n<p dir=\"ltr\">- Bạn đang học vật v&atilde; để cho 4 kỹ năng nghe n&oacute;i đọc viết được th&agrave;nh thạo nhưng m&atilde;i vẫn kh&ocirc;ng hiệu quả&nbsp;</p>\n<p dir=\"ltr\">- Cho d&ugrave; bạn đang học bằng c&aacute;ch n&agrave;o nhưng sau một thời gian nh&igrave;n lại, bạn thấy vốn từ vựng của m&igrave;nh kh&ocirc;ng kh&aacute; l&ecirc;n bao nhi&ecirc;u, 4 kỹ năng cũng chẳng hề cải thiện.</p>\n<p dir=\"ltr\"><em><strong>Vậy nguy&ecirc;n nh&acirc;n l&agrave; g&igrave;?</strong></em></p>\n<p dir=\"ltr\">C&acirc;u trả lời: đ&oacute; l&agrave; do bạn đ&atilde; chọn SAI PHƯƠNG PH&Aacute;P HỌC TIẾNG ANH&nbsp;</p>\n<p dir=\"ltr\">Kh&oacute;a học&nbsp;<em><strong>Học giỏi Tiếng Anh to&agrave;n diện: Nghe, n&oacute;i, đọc, viết</strong></em>&nbsp;của giảng vi&ecirc;n&nbsp;<em><strong>B&ugrave;i Đức Tiến</strong></em>&nbsp;sẽ gi&uacute;p bạn hệ thống lại những kiến thức vốn c&oacute; của m&igrave;nh một c&aacute;ch khoa học v&agrave; gi&uacute;p bạn nắm chắc 4 kỹ năng quan trọng của tiếng anh.</p>\n<p dir=\"ltr\"><em><strong>Nội dung kh&oacute;a học</strong></em></p>\n<p dir=\"ltr\">Phần 1: Tăng 3000 từ vựng Tiếng Anh th&ocirc;ng dụng trong 30 ng&agrave;y</p>\n<p dir=\"ltr\">Phần 2: Chắc 10 ngữ ph&aacute;p Tiếng Anh th&ocirc;ng dụng nhất trong giao tiếp</p>\n<p dir=\"ltr\">Phần 3: Chiến lược học ph&aacute;t &acirc;m chuẩn Tiếng Anh</p>\n<p dir=\"ltr\">Phần 4: 4 Chiến lược để nghe được Tiếng Anh</p>\n<p dir=\"ltr\">Phần 5: 5 Chiến lược học phản xạ n&oacute;i Tiếng Anh</p>\n<p dir=\"ltr\">Phần 6: Chiến lược đọc Tiếng Anh hiểu nhanh</p>\n<p dir=\"ltr\">Phần 7: Kỹ thuật luyện viết Tiếng Anh cho người bận rộn.</p>\n<p dir=\"ltr\">C&ograve;n chần chừ g&igrave; m&agrave; kh&ocirc;ng nhanh tay đăng k&yacute; kh&oacute;a học ngoại ngữ&nbsp;<em><strong>Học giỏi Tiếng Anh to&agrave;n diện: Nghe, n&oacute;i, đọc, viết</strong></em> c&oacute; tại <strong>UttLearning</strong><em><strong>.vn</strong></em>&nbsp;để c&oacute; thể chinh phục ng&ocirc;n ngữ tiếng Anh trong thời gian ngắn nhất.&nbsp;</p>'),
(42, 67, 'Trình chiếu chuyên nghiệp với PowerPoint Pro', 'Giúp bạn nắm được kiến thức cơ bản và nâng cao về cách sử dụng phần mềm PowerPoint, hoàn thiện kĩ năng làm bài thuyết trình ở mức độ cơ bản và nâng cao.', 26.00, '[fO5]-sddefault.webp', '[mKj]-Trình-chiếu-chuyên-nghiệp-với-PowerPoint.mp4', 19, 0, 0, 0, '[]', '<p dir=\"ltr\"><em><strong>Powerpoint l&agrave; g&igrave; ?&nbsp;</strong></em></p>\n<p dir=\"ltr\">PowerPoint l&agrave; một phần mềm tr&igrave;nh chiếu trong bộ c&ocirc;ng c<u>ụ</u>&nbsp;Microsoft Office&nbsp;đ&ocirc;ng đảo học sinh, gi&aacute;o vi&ecirc;n hay giới văn ph&ograve;ng sử dụng cho những b&agrave;i thuyết tr&igrave;nh của m&igrave;nh. C&ugrave;ng với Microsoft Word v&agrave; Microsoft Excel, Microsoft PowerPoint đ&atilde; trở th&agrave;nh một thiết bị<em>&nbsp;</em>kh&ocirc;ng thể thiếu. Bởi vậy m&agrave; việc&nbsp;học tin học văn ph&ograve;ng&nbsp;l&agrave; rất cần thiết</p>\n<p dir=\"ltr\">Phần mềm Microsoft PowerPoint ra đời với mục đ&iacute;ch ch&iacute;nh l&agrave; phục vụ c&aacute;c buổi thuyết tr&igrave;nh, thay thế cho bảng v&agrave; phấn. N&oacute; cho ph&eacute;p người d&ugrave;ng tạo ra những Slide để thể hiện những th&ocirc;ng điệp với c&aacute;c hiệu ứng xuy&ecirc;n suốt.</p>\n<p dir=\"ltr\"><em><strong>Lợi &iacute;ch khi sử dụng Powerpoint</strong></em></p>\n<p dir=\"ltr\">✔️ PowerPoint l&agrave; c&ocirc;ng cụ v&ocirc; c&ugrave;ng hữu &iacute;ch khi được sử dụng rộng r&atilde;i v&agrave; c&oacute; nhiều c&ocirc;ng dụng tuyệt vời.</p>\n<p dir=\"ltr\">✔️ Gi&uacute;p c&aacute;c doanh nghiệp tạo c&aacute;c b&agrave;i thuyết tr&igrave;nh cho sản phẩm v&agrave; dịch vụ của m&igrave;nh.</p>\n<p dir=\"ltr\">✔️ Gi&uacute;p c&aacute;c tổ chức gi&aacute;o dục tạo b&agrave;i giảng trực quan cho lớp học.</p>\n<p dir=\"ltr\">✔️ Sử dụng với mục đ&iacute;ch c&aacute; nh&acirc;n như lập bản đồ tư duy hay tr&igrave;nh chiếu ảnh gia đ&igrave;nh.</p>\n<p dir=\"ltr\">✔️ Tạo t&agrave;i liệu b&aacute;n h&agrave;ng, tiếp thị.</p>\n<p dir=\"ltr\">✔️ Tạo b&agrave;i tr&igrave;nh b&agrave;y trong những sự kiện như đ&aacute;m cưới, lễ kỷ niệm hay cuộc họp mặt gia đ&igrave;nh.</p>\n<p dir=\"ltr\">✔️ Tạo b&agrave;i thuyết tr&igrave;nh cho c&aacute;c dự &aacute;n, ng&acirc;n s&aacute;ch v&agrave; t&agrave;i ch&iacute;nh.</p>\n<p dir=\"ltr\">✔️ Tạo chứng chỉ, lịch, b&aacute;o c&aacute;o, sơ đồ, biểu đồ cho bất kỳ ng&agrave;nh n&agrave;o.</p>\n<p dir=\"ltr\">✔️ Bạn c&oacute; thể tạo hầu hết mọi loại h&igrave;nh ảnh trong PowerPoint.</p>\n<p dir=\"ltr\"><em><strong>Kh&oacute;a học Tr&igrave;nh chiếu chuy&ecirc;n nghiệp với PowerPoint c&oacute; tại Unica.vn</strong></em></p>\n<p dir=\"ltr\">Kh&oacute;a học được bi&ecirc;n soạn bởi giảng vi&ecirc;n&nbsp;<em><strong>Nguyễn&nbsp;Đức Thanh&nbsp;</strong></em>c&oacute; 43 b&agrave;i giảng, bao gồm 7 phần học ch&iacute;nh như sau:</p>\n<p dir=\"ltr\">Phần 1: Giới thiệu tổng quan</p>\n<p dir=\"ltr\">Phần 2: C&aacute;c thao t&aacute;c cơ bản với slides v&agrave; b&agrave;i thuyết tr&igrave;nh</p>\n<p dir=\"ltr\">Phần 3: C&aacute;c th&agrave;nh phần của b&agrave;i thuyết tr&igrave;nh</p>\n<p dir=\"ltr\">Phần 4: Hiệu ứng trong PowerPoint</p>\n<p dir=\"ltr\">Phần 5: Th&ecirc;m c&aacute;c th&agrave;nh phần đa phương tiện v&agrave;o trong slides</p>\n<p dir=\"ltr\">Phần 6: C&aacute;c th&agrave;nh phần điều khiển, slide master trong PowerPoint</p>\n<p dir=\"ltr\">Phần 7: Addin office mix tăng t&iacute;nh tương t&aacute;c cho b&agrave;i thuyết tr&igrave;nh powerpoint</p>\n<p dir=\"ltr\">Kh&oacute;a học sẽ gi&uacute;p c&aacute;c học vi&ecirc;n nắm được kiến thức cơ bản v&agrave; n&acirc;ng cao về c&aacute;ch sử dụng phần mềm PowerPoint, ho&agrave;n thiện kỹ năng l&agrave;m b&agrave;i thuyết tr&igrave;nh v&agrave;&nbsp;<a href=\"https://unica.vn/tag/thuyet-trinh\">học thuyết tr&igrave;nh online</a>&nbsp;ở mức độ cơ bản tới&nbsp;n&acirc;ng cao.&nbsp;</p>'),
(43, 106, 'Trình chiếu chuyên nghiệp với PowerPoint', 'Giúp bạn nắm được kiến thức cơ bản và nâng cao về cách sử dụng phần mềm PowerPoint, hoàn thiện kĩ năng làm bài thuyết trình ở mức độ cơ bản và nâng cao.', 26.00, '[ujK]-sddefault.webp', '[IOg]-Trình-chiếu-chuyên-nghiệp-với-PowerPoint.mp4', 19, 0, 1, 0, '[]', '<p dir=\"ltr\"><em><strong>Powerpoint l&agrave; g&igrave; ?&nbsp;</strong></em></p>\n<p dir=\"ltr\">PowerPoint l&agrave; một phần mềm tr&igrave;nh chiếu trong bộ c&ocirc;ng c<u>ụ</u>&nbsp;Microsoft Office&nbsp;đ&ocirc;ng đảo học sinh, gi&aacute;o vi&ecirc;n hay giới văn ph&ograve;ng sử dụng cho những b&agrave;i thuyết tr&igrave;nh của m&igrave;nh. C&ugrave;ng với Microsoft Word v&agrave; Microsoft Excel, Microsoft PowerPoint đ&atilde; trở th&agrave;nh một thiết bị<em>&nbsp;</em>kh&ocirc;ng thể thiếu. Bởi vậy m&agrave; việc&nbsp;học tin học văn ph&ograve;ng&nbsp;l&agrave; rất cần thiết</p>\n<p dir=\"ltr\">Phần mềm Microsoft PowerPoint ra đời với mục đ&iacute;ch ch&iacute;nh l&agrave; phục vụ c&aacute;c buổi thuyết tr&igrave;nh, thay thế cho bảng v&agrave; phấn. N&oacute; cho ph&eacute;p người d&ugrave;ng tạo ra những Slide để thể hiện những th&ocirc;ng điệp với c&aacute;c hiệu ứng xuy&ecirc;n suốt.</p>\n<p dir=\"ltr\"><em><strong>Lợi &iacute;ch khi sử dụng Powerpoint</strong></em></p>\n<p dir=\"ltr\">✔️ PowerPoint l&agrave; c&ocirc;ng cụ v&ocirc; c&ugrave;ng hữu &iacute;ch khi được sử dụng rộng r&atilde;i v&agrave; c&oacute; nhiều c&ocirc;ng dụng tuyệt vời.</p>\n<p dir=\"ltr\">✔️ Gi&uacute;p c&aacute;c doanh nghiệp tạo c&aacute;c b&agrave;i thuyết tr&igrave;nh cho sản phẩm v&agrave; dịch vụ của m&igrave;nh.</p>\n<p dir=\"ltr\">✔️ Gi&uacute;p c&aacute;c tổ chức gi&aacute;o dục tạo b&agrave;i giảng trực quan cho lớp học.</p>\n<p dir=\"ltr\">✔️ Sử dụng với mục đ&iacute;ch c&aacute; nh&acirc;n như lập bản đồ tư duy hay tr&igrave;nh chiếu ảnh gia đ&igrave;nh.</p>\n<p dir=\"ltr\">✔️ Tạo t&agrave;i liệu b&aacute;n h&agrave;ng, tiếp thị.</p>\n<p dir=\"ltr\">✔️ Tạo b&agrave;i tr&igrave;nh b&agrave;y trong những sự kiện như đ&aacute;m cưới, lễ kỷ niệm hay cuộc họp mặt gia đ&igrave;nh.</p>\n<p dir=\"ltr\">✔️ Tạo b&agrave;i thuyết tr&igrave;nh cho c&aacute;c dự &aacute;n, ng&acirc;n s&aacute;ch v&agrave; t&agrave;i ch&iacute;nh.</p>\n<p dir=\"ltr\">✔️ Tạo chứng chỉ, lịch, b&aacute;o c&aacute;o, sơ đồ, biểu đồ cho bất kỳ ng&agrave;nh n&agrave;o.</p>\n<p dir=\"ltr\">✔️ Bạn c&oacute; thể tạo hầu hết mọi loại h&igrave;nh ảnh trong PowerPoint.</p>\n<p dir=\"ltr\"><em><strong>Kh&oacute;a học Tr&igrave;nh chiếu chuy&ecirc;n nghiệp với PowerPoint c&oacute; tại Unica.vn</strong></em></p>\n<p dir=\"ltr\">Kh&oacute;a học được bi&ecirc;n soạn bởi giảng vi&ecirc;n&nbsp;<em><strong>Nguyễn&nbsp;Đức Thanh&nbsp;</strong></em>c&oacute; 43 b&agrave;i giảng, bao gồm 7 phần học ch&iacute;nh như sau:</p>\n<p dir=\"ltr\">Phần 1: Giới thiệu tổng quan</p>\n<p dir=\"ltr\">Phần 2: C&aacute;c thao t&aacute;c cơ bản với slides v&agrave; b&agrave;i thuyết tr&igrave;nh</p>\n<p dir=\"ltr\">Phần 3: C&aacute;c th&agrave;nh phần của b&agrave;i thuyết tr&igrave;nh</p>\n<p dir=\"ltr\">Phần 4: Hiệu ứng trong PowerPoint</p>\n<p dir=\"ltr\">Phần 5: Th&ecirc;m c&aacute;c th&agrave;nh phần đa phương tiện v&agrave;o trong slides</p>\n<p dir=\"ltr\">Phần 6: C&aacute;c th&agrave;nh phần điều khiển, slide master trong PowerPoint</p>\n<p dir=\"ltr\">Phần 7: Addin office mix tăng t&iacute;nh tương t&aacute;c cho b&agrave;i thuyết tr&igrave;nh powerpoint</p>\n<p dir=\"ltr\">Kh&oacute;a học sẽ gi&uacute;p c&aacute;c học vi&ecirc;n nắm được kiến thức cơ bản v&agrave; n&acirc;ng cao về c&aacute;ch sử dụng phần mềm PowerPoint, ho&agrave;n thiện kỹ năng l&agrave;m b&agrave;i thuyết tr&igrave;nh v&agrave;&nbsp;<a href=\"https://unica.vn/tag/thuyet-trinh\">học thuyết tr&igrave;nh online</a>&nbsp;ở mức độ cơ bản tới&nbsp;n&acirc;ng cao.&nbsp;</p>'),
(44, 70, 'Lập trình Web tốc độ cao, thời gian thực với NodeJS', 'Học lập trình Nodejs - Khóa học hướng dẫn Viết Web Server, xây dựng Blog cá nhân, tạo ứng dụng Chat web... với NodeJS.', 30.00, '[CWl]-hqdefault-(2).jpg', '[JSh]-Lập-trình-Web-tốc-độ-cao,-thời-gian-thực-với-NODEJS.mp4', 19, 30, 1, 0, '[]', '<h3>Bạn sẽ học được g&igrave;</h3>\n<div class=\"content\">\n<div class=\"row\">\n<div class=\"col-sm-6 form-group\">\n<div class=\"title\">Nắm được c&aacute;ch c&agrave;i đặt NodeJS tr&ecirc;n mọi m&ocirc;i trường</div>\n</div>\n<div class=\"col-sm-6 form-group\">\n<div class=\"title\">Biết c&aacute;ch sử dụng NPM để quản l&yacute; thư viện cho ứng dụng NodeJS</div>\n</div>\n<div class=\"col-sm-6 form-group\">\n<div class=\"title\">Biết c&aacute;ch l&agrave;m việc với NodeJS v&agrave; logic ph&iacute;a Server</div>\n</div>\n<div class=\"col-sm-6 form-group\">\n<div class=\"title\">Viết được Web Server v&agrave; ứng dụng Web cơ bản với NodeJS</div>\n</div>\n<div class=\"col-sm-6 form-group\">\n<div class=\"title\">X&acirc;y dựng BLOG c&aacute; nh&acirc;n, tạo được ứng dụng CHAT web</div>\n</div>\n<div class=\"col-sm-6 form-group\">\n<div class=\"title\">Biết c&aacute;ch triển khai ứng dụng NODEJS tr&ecirc;n Internet</div>\n<div class=\"title\">\n<h3><strong>Bạn c&oacute; biết:</strong></h3>\n<p>Node.js l&agrave; 1 nền tảng ph&aacute;t triển ứng dụng ph&iacute;a server. N&oacute; sử dụng ng&ocirc;n ngữ lập tr&igrave;nh JavaScript. Mỗi kết nối đến sẽ sinh ra 1 sự kiện, cho ph&eacute;p h&agrave;ng chục ngh&igrave;n user truy cập c&ugrave;ng l&uacute;c v&agrave; tốc độ th&igrave; cực nhanh.&nbsp;</p>\n<p>NodeJS hiện đang l&agrave; 1 Javascript Engine cực hot, được nhiều người ưa chuộng bởi tốc độ nhanh, nhẹ, đơn giản v&agrave; thư viện hỗ trợ phong ph&uacute;.&nbsp;</p>\n<h3><strong>Tuy nhi&ecirc;n:</strong></h3>\n<p>❓ Bạn đang gặp phải thắc mắc về việc tự&nbsp;<strong>học c&ocirc;ng nghệ th&ocirc;ng tin</strong>&nbsp;v&agrave; tự theo đuổi con đường lập tr&igrave;nh. Bạn nhận thấy việc tự học kh&ocirc;ng c&oacute; thầy rất kh&oacute;.</p>\n<p>❓ Bạn kh&ocirc;ng biết NodeJS l&agrave;m việc như thế n&agrave;o, kh&ocirc;ng biết c&aacute;ch tạo một webservice cơ bản</p>\n<p>❓ Bạn đang vật lộn trong việc x&acirc;y dựng v&agrave; thiết kế cơ sở dữ liệu</p>\n<p>❓ Bạn đang x&acirc;y dựng trang quản trị CSM cho blog, x&acirc;y dựng API x&oacute;a b&agrave;i viết nhưng vẫn loay hoay chưa biết c&aacute;ch xử l&yacute; như n&agrave;o?</p>\n<p>❓ Bạn kh&ocirc;ng c&oacute; một lộ tr&igrave;nh học lập tr&igrave;nh web online b&agrave;i bản v&agrave; đầy đủ.</p>\n<p>Tất cả những vấn đề tr&ecirc;n đều được giải quyết trong 6 giờ 10 ph&uacute;t với kh&oacute;a học &ldquo;<strong>Lập tr&igrave;nh Web tốc độ cao, thời gian thực với NodeJS</strong>&rdquo; của giảng vi&ecirc;n B&aacute; Ngọc Cương tr&ecirc;n UNICA để được hướng dẫn bạn từng bước để x&acirc;y dựng c&aacute;c ứng dụng Web thời gian thực, tốc độ cao: BLOG c&aacute; nh&acirc;n, CHAT nh&oacute;m,..</p>\n<p>Kh&oacute;a học bao gồm 52 b&agrave;i giảng v&agrave; được chia l&agrave;m 9 nội dung học cụ thể như sau:</p>\n<p>- Phần 1: Giới thiệu v&agrave; c&agrave;i đặt m&ocirc;i trường&nbsp;- Phần 2: L&agrave;m việc với NodeJS</p>\n<p>- Phần 3: L&agrave;m việc với ExpressJS Framework</p>\n<p>- &nbsp;Phần 4: C&agrave;i đặt v&agrave; kết nối CSDL MySQL</p>\n<p>- Phần 5: X&acirc;y dựng module Đăng K&yacute; v&agrave; Đăng nhập</p>\n<p>-&nbsp; Phần 6: X&acirc;y dựng trang quản trị CMS cho BLOG</p>\n<p>- &nbsp;Phần 7: X&acirc;y dựng BLOG c&aacute; nh&acirc;n</p>\n<p>- Phần 8: X&acirc;y dựng ứng dụng CHAT thời gian thực với NodeJS v&agrave; Socket.IO</p>\n<p>- &nbsp;Phần 9: Triển khai ứng dụng NodeJS tr&ecirc;n Server</p>\n<p>Vậy c&ograve;n chần chờ g&igrave; nữa m&agrave; kh&ocirc;ng&nbsp; đăng k&yacute; ngay kh&oacute;a học Nodejs \"<strong>Lập tr&igrave;nh Web tốc độ cao, thời gian thực với NodeJS</strong>\"&nbsp; ngay h&ocirc;m nay để Update xu thế! &nbsp;</p>\n</div>\n</div>\n</div>\n</div>'),
(45, 68, 'Quản trị cuộc đời - Đường đến thành công', 'Quản trị cuộc đời - Đường đến thành công', 22.00, '[hLz]-sddefault.webp', '[ooD]-Quản-Trị-Cuộc-Đời--Lê-Thẩm-Dương.mp4', 19, 38, 1, 0, '[]', '<h3>Bạn sẽ học được g&igrave;</h3>\n<div class=\"content\">\n<div class=\"row\">\n<div class=\"col-sm-6 form-group\">\n<div class=\"title\">Hiểu được thế n&agrave;o l&agrave; quản trị v&agrave; quản trị cuộc đời</div>\n</div>\n<div class=\"col-sm-6 form-group\">\n<div class=\"title\">Nắm r&otilde; quy tr&igrave;nh thực hiện mục ti&ecirc;u</div>\n</div>\n<div class=\"col-sm-6 form-group\">\n<div class=\"title\">C&oacute; được khả năng định vị bản th&acirc;n một c&aacute;ch ch&iacute;nh x&aacute;c v&agrave; nhanh ch&oacute;ng</div>\n</div>\n<div class=\"col-sm-6 form-group\">\n<div class=\"title\">Nắm được 4 chiến lược thực hiện mục ti&ecirc;u chiếc lược cuộc đời</div>\n</div>\n<div class=\"col-sm-6 form-group\">\n<div class=\"title\">Biết được phương ph&aacute;p, c&aacute;ch thức thực hiện mục ti&ecirc;u th&agrave;nh c&ocirc;ng</div>\n</div>\n<div class=\"col-sm-6 form-group\">\n<div class=\"title\">Kh&aacute;m ph&aacute; gi&aacute; trị bản th&acirc;n v&agrave; vận dụng nguồn lực sẵn c&oacute;</div>\n<div class=\"title\">\n<h3><strong>Bạn c&oacute; biết:</strong></h3>\n<p>Nghề - &nbsp;C&ocirc;ng cụ - Kỹ năng mềm l&agrave; 3 yếu tố &nbsp;gi&uacute;p bạn bước đến th&agrave;nh c&ocirc;ng. Sở hữu 3 yếu tố n&agrave;y l&agrave; một điều v&ocirc; c&ugrave;ng tuyệt vời, nhưng ch&uacute;ng sẽ c&ograve;n tuyệt vời hơn nếu bạn biết c&aacute;ch nh&igrave;n nhận v&agrave;&nbsp;kiểm so&aacute;t, kết hợp cũng như ph&acirc;n bổ ch&uacute;ng - những nguồn lực một c&aacute;ch h&agrave;i h&ograve;a, hiệu quả&nbsp;ch&uacute;ng một c&aacute;ch hiệu quả sẽ l&agrave; h&agrave;nh trang tuyệt vời gi&uacute;p bạn ho&agrave;n th&agrave;nh được mục ti&ecirc;u dựa tr&ecirc;n nguồn lực m&agrave; bạn c&oacute;.</p>\n<p>Đến với kh&oacute;a học&nbsp;<strong>\"Quản trị cuộc đời - Đường đến th&agrave;nh c&ocirc;ng\",&nbsp;</strong>TS.L&ecirc; Th&acirc;̉m Dương<strong>&nbsp;</strong>sẽ gi&uacute;p&nbsp;bạn c&oacute; được c&aacute;i nh&igrave;n tổng quan nhất về g&oacute;i h&agrave;nh trang ấy, gi&uacute;p bạn nhận ra được ho&agrave;i b&atilde;o mong muốn của bản th&acirc;n&nbsp;m&igrave;nh. Kh&ocirc;ng chỉ c&oacute; vậy bạn c&ograve;n c&oacute; được những chiến lược cuộc đời, định vị v&agrave; thực hiện ch&uacute;ng một c&aacute;ch ho&agrave;n hảo nhất.</p>\n<h3><strong>Nội dung kh&oacute;a học&nbsp;</strong><strong>Quản trị cuộc đời - Đường đến th&agrave;nh c&ocirc;ng</strong></h3>\n<p>Kh&oacute;a học quản trị thời gian L&ecirc; Thẩm Dương&nbsp;bao gồm 11 b&agrave;i giảng v&agrave; thời lượng học l&agrave; 02 giờ 31 ph&uacute;t. Kh&oacute;a học mua một lần sở hữu trọn đời.</p>\n<p>Với mong muốn chia sẻ kiến thức v&agrave; kinh nghiệm sống thực tế tới tất cả mọi người, đặc biệt l&agrave; với c&aacute;c bạn trẻ, TS.L&ecirc; Th&acirc;̉m Dương sẽ gi&uacute;p bạn gi&uacute;p mọi người định vị lại ch&iacute;nh m&igrave;nh v&agrave; cuộc đời m&igrave;nh, biết tr&acirc;n trọng v&agrave; y&ecirc;u qu&yacute; hơn những gi&aacute; trị ri&ecirc;ng c&oacute; của bản th&acirc;n để tự tin quản trị v&agrave; sống cuộc đời của ch&iacute;nh m&igrave;nh. Mỗi một c&aacute; thể l&agrave; một thế giới ri&ecirc;ng kh&ocirc;ng ai c&oacute; thể hiểu được bạn ngo&agrave;i ch&iacute;nh bản th&acirc;n bạn,&nbsp;nhiệm vụ của ch&uacute;ng ta l&agrave; kh&aacute;m ph&aacute; n&oacute; v&agrave; th&agrave;nh c&ocirc;ng, hạnh ph&uacute;c đều khởi nguồn từ h&agrave;nh tr&igrave;nh kh&aacute;m ph&aacute; v&ocirc; tận n&agrave;y.&nbsp;</p>\n<p>H&atilde;y đến với kh&oacute;a học&nbsp;<strong>\"Quản trị cuộc đời - Đường đến th&agrave;nh c&ocirc;ng\"</strong>&nbsp;của TS. L&ecirc; Thẩm Dương tại Unica.vn. Đ&acirc;y l&agrave; nơi để bạn kh&aacute;m ph&aacute; được gi&aacute; trị của bản th&acirc;n v&agrave; t&igrave;m kiếm con đường th&agrave;nh c&ocirc;ng cho ch&iacute;nh m&igrave;nh.&nbsp;</p>\n</div>\n</div>\n</div>\n</div>'),
(46, 164, 'Dạy con siêu trí nhớ cùng kỷ lục gia', 'Học siêu trí nhớ cùng kỷ lục gia Nguyễn Phùng Phong sẽ Khai thác hết tất cả những tiềm năng của não bộ. Khóa học Siêu trí nhớ giúp con thuộc bài ngay tại lớp, ghi nhớ 100 từ vựng tiếng Anh mỗi ngày.', 19.00, '[8xi]-maxresdefault.webp', '[0Rh]-Dạy-con-siêu-trí-nhớ-cùng-kỷ-lục-gia---Nguyễn-Hùng-Phong.mp4', 19, 67, 1, 0, '[]', '<h3>Bạn sẽ học được g&igrave;</h3>\n<div class=\"content\">\n<div class=\"row\">\n<div class=\"col-sm-6 form-group\">\n<div class=\"title\">T&iacute;nh nhẩm nhanh ch&oacute;ng</div>\n</div>\n<div class=\"col-sm-6 form-group\">\n<div class=\"title\">C&aacute;c con sẽ thuộc b&agrave;i ngay tại lớp</div>\n</div>\n<div class=\"col-sm-6 form-group\">\n<div class=\"title\">Si&ecirc;u tr&iacute; nhớ ghi nhớ 100 từ vựng tiếng anh mỗi ng&agrave;y một c&aacute;ch dễ d&agrave;ng</div>\n</div>\n<div class=\"col-sm-6 form-group\">\n<div class=\"title\">Khai th&aacute;c hết tất cả những tiềm năng của n&atilde;o bộ c&aacute;c con</div>\n<div class=\"title\">\n<p><strong>C&aacute;c bậc phụ huynh lu&ocirc;n mong muốn:</strong></p>\n<p>✔️ L&agrave; cha mẹ, bạn lu&ocirc;n mong muốn con m&igrave;nh được th&agrave;nh c&ocirc;ng v&agrave; hạnh ph&uacute;c.</p>\n<p>✔️ H&agrave;ng ng&agrave;y con bạn phải mất h&agrave;ng giờ để học b&agrave;i m&agrave; kết quả vẫn kh&ocirc;ng như mong đợi.</p>\n<p>✔️ Bạn mong muốn c&oacute; một giải ph&aacute;p gi&uacute;p con vừa học tốt m&agrave; vẫn c&oacute; thể vui chơi.</p>\n<p>Tuy nhi&ecirc;n c&oacute; rất nhiều r&agrave;o cản tr&ecirc;n con đường để con bạn ph&aacute;t triển v&agrave; th&agrave;nh t&agrave;i. Ch&iacute;nh v&igrave; thế, ch&uacute;ng t&ocirc;i muốn giới thiệu bạn, kh&oacute;a học \"<strong>Dạy con si&ecirc;u tr&iacute; nhớ c&ugrave;ng kỷ lục gia</strong>\" của giảng vi&ecirc;n&nbsp;<strong>Nguyễn Ph&ugrave;ng Phong</strong>&nbsp;tr&ecirc;n UNICA sẽ đem đến cho c&aacute;c con 1 thay đổi lớn</p>\n<p>Con bạn sẽ trở n&ecirc;n nổi bật hơn v&agrave; được bạn b&egrave; ngưỡng mộ với tr&iacute; nhớ si&ecirc;u ph&agrave;m. Con sẽ biết c&aacute;ch kh&aacute;m ph&aacute; nguy&ecirc;n l&yacute; hoạt động của n&atilde;o bộ v&agrave; khai th&aacute;c n&atilde;o bộ hiệu quả. C&aacute;c con sẽ được kh&iacute;ch lệ tinh thần, thỏa sức s&aacute;ng tạo v&agrave; cải thiện được sự tập trung trong học tập.</p>\n<p>Kh&oacute;a học &ldquo;Dạy con si&ecirc;u tr&iacute; nhớ c&ugrave;ng kỷ lục gia&rdquo; &nbsp;bao gồm 19 b&agrave;i giảng v&agrave; chia l&agrave;m 3 phần r&otilde; r&agrave;ng, chi tiết, dễ hiểu, dễ theo học.</p>\n<p>- Phần 1: Luyện tập th&oacute;i quen để si&ecirc;u tr&iacute; nhớ: Trẻ sẽ được hướng dẫn c&aacute;ch học c&aacute;c con số, c&aacute;ch đếm kiểu H&agrave;n Quốc, c&aacute;c b&agrave;i tập k&iacute;ch hoạt b&aacute;n cầu n&atilde;o, c&aacute;ch chuyển c&aacute;c con số th&agrave;nh h&igrave;nh ảnh.</p>\n<p>- Phần 2: Thực h&agrave;nh si&ecirc;u tr&iacute; nhớ: Trẻ được r&egrave;n luyện c&aacute;c b&agrave;i tập của c&aacute;c kỷ lục gia si&ecirc;u tr&iacute; tuệ tr&ecirc;n thế giới.</p>\n<p>- Phần 3: Con đường trở th&agrave;nh Kỷ lục gia: Trẻ sẽ được giảng vi&ecirc;n hướng dẫn c&aacute;c b&agrave;i tập luyện mắt, luyện gi&atilde;n đồng tử, c&aacute;ch tăng năng lượng để học tốt.</p>\n<p>Với lộ tr&igrave;nh nu&ocirc;i dạy con r&otilde; r&agrave;ng như vậy,&nbsp;<strong>h&atilde;y đăng k&yacute;</strong>&nbsp;để tương lai của con bạn trở n&ecirc;n tốt đẹp hơn!</p>\n</div>\n</div>\n</div>\n</div>'),
(47, 120, 'Tự do tài chính cùng chứng khoán', 'Khoá học đầu tư tài chính đến từ chuyên gia Đặng Trọng Khang \"Tự do tài chính cùng chứng khoán\" giúp bạn tránh được những rủi ro, cạm bẫy để tăng thu nhập nhờ chứng khoán.', 29.00, '[Ryh]-sddefault.webp', '[2zS]-Tự-do-tài-chính-cùng-chứng-khoán_Đặng-Trọng-Khang.mp4', 19, 33, 1, 0, '[]', '<h3>Bạn sẽ học được g&igrave;</h3>\n<div class=\"content\">\n<div class=\"row\">\n<div class=\"col-sm-6 form-group\">\n<div class=\"title\">Tư duy đ&uacute;ng trong đầu tư cổ phiếu</div>\n</div>\n<div class=\"col-sm-6 form-group\">\n<div class=\"title\">Tr&aacute;nh được những rủi ro, cạm bẫy tr&ecirc;n TTCK</div>\n</div>\n<div class=\"col-sm-6 form-group\">\n<div class=\"title\">Hiểu được 04 nguồn thu nhập từ đầu tư</div>\n</div>\n<div class=\"col-sm-6 form-group\">\n<div class=\"title\">Chiến lược đầu tư tạo thu nhập thụ động từ cổ phiếu</div>\n</div>\n<div class=\"col-sm-6 form-group\">\n<div class=\"title\">Dễ d&agrave;ng tham gia v&agrave;o nh&oacute;m 05% những nh&agrave; đầu tư chiến thắng tr&ecirc;n thị trường</div>\n</div>\n<div class=\"col-sm-6 form-group\">\n<div class=\"title\">Khả năng đầu tư thực tế th&agrave;nh c&ocirc;ng ngay sau kh&oacute;a học</div>\n<div class=\"title\">\n<p><em>Bạn l&agrave; người mới bắt đầu học kinh doanh&nbsp;v&agrave;&nbsp;l&agrave;m quen với chứng kho&aacute;n ?</em></p>\n<p><em>Bạn chưa c&oacute; nhiều kiến thức hay kinh nghiệm về thị trường chứng kho&aacute;n?</em></p>\n<p><em>Bạn muốn tăng thu nhập nhanh ch&oacute;ng từ chứng kho&aacute;n nhưng kh&ocirc;ng c&oacute; kiến thức đầu tư ?</em></p>\n<p><em>Hay bạn muốn đầu tư cổ phiếu nhưng kh&ocirc;ng biết lựa chọn loại cổ phiếu v&agrave; thời điểm mua v&agrave;o để sinh lợi?</em></p>\n<p><em>Bạn đang t&igrave;m kiếm một&nbsp;kh&oacute;a học đầu tư t&agrave;i ch&iacute;nh ph&ugrave; hợp nhưng kh&ocirc;ng biết t&igrave;m ở đ&acirc;u?</em></p>\n<p><em><strong>Bạn đừng qu&aacute; lo lắng,&nbsp;</strong></em></p>\n<p>Bởi kh&oacute;a học \"<em><strong>Tự do t&agrave;i ch&iacute;nh c&ugrave;ng chứng kho&aacute;n\"&nbsp;</strong></em>của giảng vi&ecirc;n&nbsp;<em><strong>Đặng Trọng Khang</strong></em>&nbsp;sẽ gi&uacute;p bạn về c&aacute;c vấn đề cơ bản d&agrave;nh cho người mới bắt đầu, cung cấp tư duy đ&uacute;ng trong đầu tư chứng kho&aacute;n, mang đến một c&ocirc;ng thức kỳ diệu chinh phục thị trường chứng kho&aacute;n. V&agrave; đặc biệt l&agrave; bật m&iacute; b&iacute; quyết tạo thụ nhập thụ động, tự do t&agrave;i ch&iacute;nh từ đầu tư cổ phiếu. Đặc biệt trong kh&oacute;a học đầu tư t&agrave;i ch&iacute;nh sẽ chia sẻ&nbsp;những b&iacute; quyết tr&aacute;nh rủi ro hiệu quả nhất để tối thiểu thất tho&aacute;t v&agrave; th&agrave;nh c&ocirc;ng trong lĩnh vực đầu tư chứng kho&aacute;n.</p>\n<p><em><strong>Nội dung kh&oacute;a&nbsp;kh&oacute;a học đầu tư t&agrave;i ch&iacute;nh</strong></em></p>\n<p>Gi&aacute;o tr&igrave;nh được bi&ecirc;n soạn với 31 b&agrave;i giảng bao gồm c&aacute;c nội dung ch&iacute;nh sau đ&acirc;y:</p>\n<p>Phần 1: Nhưng sai l&agrave;m mất tiền của nh&agrave; đầu tư chứng kho&aacute;n</p>\n<p>Phần 2: Những ưu thế tuyệt vời của đầu tư chứng kho&aacute;n</p>\n<p>Phần 3: Nguy&ecirc;n l&yacute; đầu tư tạo lợi nhuận trường tồn</p>\n<p>Phần 4: C&ocirc;ng thức đầu tư kỳ diệu</p>\n<p>Phần 5: V&iacute; dụ thực tế</p>\n<p>Phần 6: Bước tiếp theo l&agrave; g&igrave;.&nbsp;</p>\n<p>Th&ecirc;m một điểm đặc biệt của kh&oacute;a kh&oacute;a học đầu tư t&agrave;i ch&iacute;nh&nbsp;l&agrave; mua một lần sở hữu trọn đời. Bạn c&oacute; thể học mọi l&uacute;c, mọi nơi khi chỉ cần m&aacute;y t&iacute;nh hoặc c&aacute;c thiết bị di động c&oacute; kết nối Internet. Điều n&agrave;y gi&uacute;p bạn chủ động hơn trong việc học, tiết kiệm thời gian, chi ph&iacute; so với c&aacute;c h&igrave;nh thức học offline tại c&aacute;c trung t&acirc;m.&nbsp;</p>\n</div>\n</div>\n</div>\n</div>'),
(48, 78, 'Tự học tiếng Trung cơ bản', 'Khóa học Tự học tiếng Trung cơ bản cho người mới bắt đầu giúp bạn nắm vững kỹ năng phát âm chuẩn, ngữ pháp và từ vựng cơ bản tương ứng HSK2, tăng khả năng phản xạ khi giao tiếp. Dễ hiểu - Hiệu quả - Nói bài bản ngay từ bài đầu tiên.', 29.00, '[rUw]-maxresdefault.webp', '[nQz]-Tự-học-Tiếng-Trung-cơ-bản---ThanhmaiHsk.mp4', 19, 38, 1, 0, '[]', '<h3>Bạn sẽ học được g&igrave;</h3>\n<div class=\"content\">\n<div class=\"row\">\n<div class=\"col-sm-6 form-group\">\n<div class=\"title\">N&oacute;i tiếng Trung b&agrave;i bản với giọng ph&aacute;t &acirc;m chuẩn ngay từ b&agrave;i đầu ti&ecirc;n</div>\n</div>\n<div class=\"col-sm-6 form-group\">\n<div class=\"title\">Tiếp thu ngữ ph&aacute;p tiếng Trung một c&aacute;ch hệ thống v&agrave; tự nhi&ecirc;n nhất</div>\n</div>\n<div class=\"col-sm-6 form-group\">\n<div class=\"title\">H&igrave;nh th&agrave;nh tư duy phản xạ ho&agrave;n to&agrave;n bằng tiếng Trung.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</div>\n</div>\n<div class=\"col-sm-6 form-group\">\n<div class=\"title\">Đủ lượng từ v&agrave; tự &ocirc;n thi HSK2 sau 5 tiếng học tập nhẹ nh&agrave;ng.</div>\n<div class=\"title\">\n<h3><strong>Bạn đang gặp phải một số vấn đề:&nbsp;</strong></h3>\n<p>❓ Bạn chưa học tiếng Trung bao giờ nhưng lại muốn học ngoại ngữ online nhanh v&agrave; tự học một c&aacute;ch b&agrave;i bản?</p>\n<p>❓ Bạn kh&ocirc;ng c&oacute; thời gian cố định để đi học v&agrave;o khung giờ cố định tại c&aacute;c trung t&acirc;m? Bạn đ&atilde; tiếp x&uacute;c với tiếng Trung một thời gian ngắn những chưa tự tin với việc ph&aacute;t &acirc;m hoặc ph&aacute;t &acirc;m sai?</p>\n<p>❓ Bạn đ&atilde; học một thời gian nhưng vẫn chưa giao tiếp được hoặc vẫn n&oacute;i sai ngữ ph&aacute;p? &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</p>\n<p>❓ Bạn mong muốn trong thời gian ngắn nhất học tiếng Trung giao tiếp hiệu quả để phục vụ nhu cầu c&ocirc;ng việc, du lịch, sở th&iacute;ch...?</p>\n<p>❓&nbsp;Bạn mong muốn t&igrave;m một kho&aacute;&nbsp;học tiếng Trung trực tuyến nhưng chưa t&igrave;m được lựa chọn ph&ugrave; hợp?</p>\n<p>Để giải quyết những vấn đề tr&ecirc;n, bạn h&atilde;y đến với Kh&oacute;a học:&nbsp;<strong>Tự học tiếng Trung cơ bản</strong>&nbsp;của giảng vi&ecirc;n Thanh Mai HSK tr&ecirc;n UNICA để được truyền cảm hứng v&agrave; chuẩn bị một h&agrave;nh trang ng&ocirc;n ngữ vững v&agrave;ng!</p>\n<h3><strong>Nội dung kh&oacute;a&nbsp;học tiếng Trung online cho người mới bắt đầu</strong></h3>\n<p>Kh&oacute;a học tiếng Trung trực tuyến&nbsp;ra đời với mục đ&iacute;ch gi&uacute;p c&aacute;c bạn tiết kiệm được thời gian, c&ocirc;ng sức, tiền bạc, nhưng vẫn đảm bảo c&aacute;c bạn nắm vững v&agrave; sử dụng tiếng Trung một c&aacute;ch th&agrave;nh thạo.</p>\n<p>Bạn sẽ kh&ocirc;ng phải mất thời gian đi lại để tham gia c&aacute;c kho&aacute; học tập trung hoặc đ&oacute;ng tiền học rồi m&agrave; vẫn phải lo lắng kh&ocirc;ng biết gi&aacute;o vi&ecirc;n ở đ&oacute; ph&aacute;t &acirc;m thế n&agrave;o, dạy c&oacute; dễ hiểu kh&ocirc;ng, hiệu quả ra sao.</p>\n<p>Kh&oacute;a học tiếng Trung qua mạng&nbsp;với những b&agrave;i giảng được bi&ecirc;n soạn tổng hợp tinh t&uacute;y từ c&aacute;c gi&aacute;o tr&igrave;nh sử dụng phổ biến hiện nay, với những mẫu c&acirc;u đối thoại ngắn gọn, x&uacute;c t&iacute;ch, những h&igrave;nh ảnh trực quan đơn giản, gi&uacute;p tăng khả năng ghi nhớ chữ H&aacute;n, nắm vững ngữ ph&aacute;p Tiếng Trung v&agrave; biết c&aacute;ch sử dụng tiếng Trung nhanh hơn.</p>\n<p>V&agrave; điều quan trọng nhất l&agrave;, chỉ với mức đầu tư v&ocirc; c&ugrave;ng thấp, bạn đ&atilde; c&oacute; cơ hội để trở th&agrave;nh học vi&ecirc;n th&agrave;nh c&ocirc;ng tiếp theo của Thanh Mai HSK - một trung t&acirc;m tiếng Trung uy t&iacute;n h&agrave;ng đầu H&agrave; Nội - TP Hồ Ch&iacute; Minh hiện nay.</p>\n<p>Ngay h&ocirc;m nay, đừng chần chừ m&agrave; h&atilde;y đăng k&yacute; kh&oacute;a học &ldquo;<strong>Tự học tiếng Trung cơ bản</strong>&rdquo;!</p>\n</div>\n</div>\n</div>\n</div>'),
(49, 168, 'Nghệ thuật Phòng the đỉnh cao', 'Học cùng cô Vera Hà Anh để sở hữu bí kíp bí mật, nghệ thuật phòng the cho phụ nữ - kỹ năng yêu \"yêu\"- giữ ngọn lửa tổ ấm gia đình thời hiện đại', 38.00, '[E9R]-sddefault.webp', '[ksA]-Nghệ-thuật-Phòng-the-đỉnh-cao---Vera-Hà-Anh.mp4', 19, 0, 1, 0, '[]', '<h3>Bạn sẽ học được g&igrave;</h3>\n<div class=\"content\">\n<div class=\"row\">\n<div class=\"col-sm-6 form-group\">\n<div class=\"title\">C&oacute; được sự h&ograve;a hợp trong t&igrave;nh dục, biết ứng xử tốt nhất \"chuyện ấy\"&nbsp;với bạn đời</div>\n</div>\n<div class=\"col-sm-6 form-group\">\n<div class=\"title\">Nắm vững c&aacute;c nguy&ecirc;n tắc v&agrave;ng để giữ lửa &ldquo;y&ecirc;u&rdquo;, 2 b&iacute; quyết gi&uacute;p cuộc &ldquo;y&ecirc;u&rdquo; cuồng nhiệt</div>\n</div>\n<div class=\"col-sm-6 form-group\">\n<div class=\"title\">Sử dụng hiệu quả sức mạnh của lời khen, kết hợp ho&agrave;n hảo ng&ocirc;n ngữ cơ thể trong khi y&ecirc;u</div>\n</div>\n<div class=\"col-sm-6 form-group\">\n<div class=\"title\">Biết c&aacute;ch &ldquo;h&ocirc;n neo&rdquo; trong qu&aacute; tr&igrave;nh l&agrave;m t&igrave;nh, trở th&agrave;nh Nữ Ho&agrave;ng ph&ograve;ng the</div>\n</div>\n<div class=\"col-sm-6 form-group\">\n<div class=\"title\">Nắm r&otilde; c&aacute;c kỹ thuật kiểm so&aacute;t xuất tinh nhanh, chậm</div>\n</div>\n<div class=\"col-sm-6 form-group\">\n<div class=\"title\">Nắm vững 7 điều quyết định cho &ldquo;bữa tiệc&rdquo; thăng hoa</div>\n</div>\n<div class=\"col-sm-6 form-group\">\n<div class=\"title\">Tr&aacute;nh những Sai lầm kh&ocirc;ng đ&aacute;ng c&oacute; của phụ nữ khi cấm vận chồng</div>\n</div>\n<div class=\"col-sm-6 form-group\">\n<div class=\"title\">Chủ động biết khi n&agrave;o c&oacute; thai, khi n&agrave;o kh&ocirc;ng&nbsp;</div>\n</div>\n<div class=\"col-sm-6 form-group\">\n<div class=\"title\">Khắc phục c&aacute;c nguy&ecirc;n nh&acirc;n g&acirc;y giảm sự ham muốn ở người đ&agrave;n &ocirc;ng</div>\n</div>\n<div class=\"col-sm-6 form-group\">\n<div class=\"title\">Hiểu r&otilde; t&acirc;m sinh l&yacute; đ&agrave;n &ocirc;ng, đ&agrave;n b&agrave; theo độ tuổi ...</div>\n<div class=\"title\">\n<p dir=\"ltr\">Xin ch&agrave;o c&aacute;c T&igrave;nh y&ecirc;u!</p>\n<p dir=\"ltr\">T&ocirc;i l&agrave; Vera H&agrave; Anh &ndash; Diễn giả, Chuy&ecirc;n gia T&acirc;m l&yacute; H&ocirc;n nh&acirc;n v&agrave; Gia đ&igrave;nh.</p>\n<p dir=\"ltr\">T&ocirc;i giới thiệu kh&oacute;a học&nbsp;<strong><em>Nghệ thuật Ph&ograve;ng the đỉnh cao</em></strong>&nbsp;với mong muốn trao tặng niềm hạnh ph&uacute;c v&agrave; t&igrave;nh y&ecirc;u thương v&ocirc; bờ tới bạn th&ocirc;ng qua những kiến thức tối thiểu về chuyện ph&ograve;ng the m&agrave; người phụ nữ cần phải nắm.</p>\n<p dir=\"ltr\">T&igrave;nh dục l&agrave; sự quan t&acirc;m, sự tin tưởng lẫn nhau giữa vợ v&agrave; chồng l&agrave; sự kết nối s&acirc;u của hai t&acirc;m hồn bạn v&agrave; anh ấy. Bạn thờ ơ với sex v&agrave; thiếu kiến thức về n&oacute; kh&ocirc;ng kh&aacute;c g&igrave; bạn bỏ rơi bạn đời tr&ecirc;n ch&iacute;nh chiếc giường anh ấy đang nằm.</p>\n<p dir=\"ltr\">Khi &ldquo;đ&oacute;i&rdquo; t&igrave;nh dục l&agrave;m sức khỏe của người đ&agrave;n &ocirc;ng giảm s&uacute;t, bởi v&igrave; họ kh&ocirc;ng happy, lu&ocirc;n ức chế, l&agrave;m cho chất lượng cuộc sống h&ocirc;n nh&acirc;n k&eacute;m đi. V&ocirc; t&igrave;nh bạn đang &ldquo;giết chết&rdquo; h&ocirc;n nh&acirc;n.</p>\n<p dir=\"ltr\">Đến với kh&oacute;a học&nbsp;<em><strong>Nghệ Thuật Ph&ograve;ng The Đỉnh Cao</strong></em>&nbsp;bạn sẽ hiểu được:</p>\n<p dir=\"ltr\">- L&atilde;nh đạo v&agrave; dẫn dắt bạn đời trong t&igrave;nh dục</p>\n<p dir=\"ltr\">- Thay đổi tận gốc tư duy, suy nghĩ t&iacute;ch cực, văn minh về t&igrave;nh dục để đời sống vợ chồng trở n&ecirc;n hạnh ph&uacute;c, trọn vẹn v&agrave; vi&ecirc;n m&atilde;n hơn</p>\n<p dir=\"ltr\">- &Yacute; nghĩa của t&igrave;nh dục &ndash; con số b&iacute; mật về tần suất, thời gian &ldquo;y&ecirc;u&rdquo;, độ tuổi, t&acirc;m sinh l&yacute;, c&aacute;c điểm nhạy cảm&hellip;</p>\n<p dir=\"ltr\">- Nghệ thuật ph&ograve;ng the cho phụ nữ khiến c&aacute;c điểm nhạy cảm thăng hoa, đạt kho&aacute;i cảm, chống sự nh&agrave;m ch&aacute;n với 365 tư thế &ldquo;y&ecirc;u&rdquo; hạnh ph&uacute;c</p>\n<p dir=\"ltr\">- Gia tăng cảm gi&aacute;c mới mẻ</p>\n<p dir=\"ltr\">- Nghệ thuật quan hệ bằng miệng giữ sức khỏe, massage</p>\n<p dir=\"ltr\">- Những c&aacute;ch &aacute;p dụng tử huyệt ng&ocirc;n từ để c&agrave;i đặt v&agrave;o tiềm thức của chồng l&uacute;c thăng hoa nhằm &ldquo;Neo&rdquo; lại cảm x&uacute;c t&iacute;ch cực.</p>\n<p dir=\"ltr\">- Gạt bỏ tư tưởng truyền thống, thực sự đ&oacute;n nhận những điều văn minh từ NGỌN LỬA D&Agrave;NH CHO TỔ ẤM THỜI HIỆN ĐẠI kh&ocirc;ng bị ph&aacute;n x&eacute;t li&ecirc;n quan đến tư c&aacute;ch hay đạo đức?</p>\n<p dir=\"ltr\">- Biến sex th&agrave;nh ngọn lửa đam m&ecirc; chăn gối của cả hai vợ chồng.</p>\n<p dir=\"ltr\">Kh&ocirc;ng ai c&oacute; thể l&agrave;m thay bạn điều n&agrave;y cả. Kể cả cha mẹ ruột thịt, con c&aacute;i, hay người m&agrave; bạn tin y&ecirc;u nhất cũng kh&ocirc;ng thể l&agrave;m thay bạn được. Bởi t&igrave;nh dục l&agrave; duy nhất v&agrave; l&agrave; của ri&ecirc;ng hai vợ chồng bạn m&agrave; th&ocirc;i! N&oacute; thi&ecirc;ng li&ecirc;ng v&agrave; cũng rất sexy đ&uacute;ng kh&ocirc;ng n&agrave;o!</p>\n<p dir=\"ltr\">H&atilde;y vứt bỏ tất cả những thứ gọi l&agrave; cổ hủ cả về quan điểm cũng như nh&agrave;m ch&aacute;n cả về tư thế v&agrave; h&atilde;y lu&ocirc;n khao kh&aacute;t đ&oacute;n nhận c&aacute;i mới mang về tặng chồng.</p>\n<p dir=\"ltr\">Đam m&ecirc; v&agrave; y&ecirc;u thương đủ lớn sẽ tạo ra nguồn cảm x&uacute;c m&atilde;nh liệt. Tất cả c&aacute;c nghệ thuật ph&ograve;ng the cho phụ nữ,&nbsp;tuyệt chi&ecirc;u sẽ c&oacute; hết trong kh&oacute;a học&nbsp;<strong><em>Nghệ thuật ph&ograve;ng the đỉnh cao</em></strong>&nbsp;n&agrave;y!</p>\n<p dir=\"ltr\">Vậy th&igrave; c&ograve;n chần chừ g&igrave; nữa m&agrave; kh&ocirc;ng&nbsp;học đời sống vợ chồng&nbsp;c&ugrave;ng Unica? HỌC v&agrave; H&Agrave;NH ngay th&ocirc;i!</p>\n</div>\n</div>\n</div>\n</div>'),
(50, 135, 'Bí quyết chốt đơn thành công 90% - Telesale, Bán hàng online', 'Khóa học cung cấp cho bạn 4 bước xây dựng kịch bản bán hàng và 7 chiến lược chốt sale khiến khách hàng không thể cưỡng lại được. Tăng tỉ lệ chốt đơn lên 90%.', 25.00, '[pSy]-maxresdefault.webp', '[wyh]-Bí-quyết-Chốt-Đơn-thành-công-90%---Telesale,-Bán-hàng-Online--(Trần-Văn-Tuấn).mp4', 19, 0, 1, 0, '[]', '<h3>Bạn sẽ học được g&igrave;</h3>\n<div class=\"content\">\n<div class=\"row\">\n<div class=\"col-sm-6 form-group\">\n<div class=\"title\">Biết c&aacute;ch thức b&aacute;n h&agrave;ng mới</div>\n</div>\n<div class=\"col-sm-6 form-group\">\n<div class=\"title\">Tăng tỷ lệ chốt đơn, giảm tỷ lệ ho&agrave;n đơn</div>\n</div>\n<div class=\"col-sm-6 form-group\">\n<div class=\"title\">Thu nhập tăng gấp 2 lần</div>\n</div>\n<div class=\"col-sm-6 form-group\">\n<div class=\"title\">Tiết kiệm thời gian khi chốt đơn</div>\n<div class=\"title\">\n<h3 dir=\"ltr\"><strong>Bạn c&oacute; biết:</strong></h3>\n<p dir=\"ltr\">70% người b&aacute;n h&agrave;ng gặp kh&oacute; khăn v&agrave;o giai đoạn chốt đơn, đ&oacute; l&agrave; bởi họ thiếu những kỹ năng cần thiết để khai th&aacute;c t&acirc;m l&yacute; kh&aacute;ch h&agrave;ng từ đ&oacute; tăng tỷ lệ chốt sales th&agrave;nh c&ocirc;ng.</p>\n<p dir=\"ltr\">Ngay cả những chủ doanh nghiệp hay những người l&agrave;m kinh doanh l&acirc;u năm vẫn c&ograve;n gặp kh&oacute; khăn để nhận được sự đồng &yacute; của kh&aacute;ch h&agrave;ng bởi sự thay đổi của thị trường đ&ograve;i hỏi ch&uacute; trọng những kỹ năng đặc biệt phục vụ cho việc chốt đơn v&agrave; tăng doanh thu.</p>\n<h3 dir=\"ltr\"><strong>Vậy:</strong></h3>\n<p dir=\"ltr\">Để tăng tỉ lệ chốt đơn cao th&igrave; doanh nghiệp, c&aacute; nh&acirc;n cần l&agrave;m g&igrave; để chuyển đổi đơn h&agrave;ng cho m&igrave;nh. Việc trang bị c&aacute;c kỹ năng b&aacute;n h&agrave;ng, kỹ năng giao tiếp&hellip; rất quan trọng.</p>\n<p dir=\"ltr\"><strong>❗ Khi gọi điện chốt đơn h&agrave;ng</strong></p>\n<p dir=\"ltr\">Kỹ năng gọi điện của bạn diễn ra theo 3 bước đ&oacute; l&agrave; &ldquo;Bạn - b&agrave;n - b&aacute;n&rdquo;. C&oacute; nghĩa l&agrave; bạn cần điều chỉnh &acirc;m thanh thật dễ nghe, cuốn h&uacute;t để duy tr&igrave; cuộc n&oacute;i chuyện thuận lợi. H&atilde;y cố gắng điều chỉnh m&agrave;n giới thiệu trong 15 đến 30 gi&acirc;y đầu ti&ecirc;n. Nếu kh&aacute;ch h&agrave;ng từ chối thay v&igrave; nản l&ograve;ng th&igrave; bạn n&ecirc;n vui vẻ đưa ra th&ecirc;m một số c&acirc;u hỏi kh&aacute;c li&ecirc;n quan tới quyền lợi của kh&aacute;ch h&agrave;ng.</p>\n<p dir=\"ltr\"><strong>❗</strong>&nbsp;<strong>Ph&acirc;n t&iacute;ch sản phẩm</strong></p>\n<p dir=\"ltr\">Khi kh&aacute;ch h&agrave;ng đ&atilde; lắng nghe, h&atilde;y đưa ra những lợi &iacute;ch sản phẩm, đồng thời chuẩn bị trước hững c&acirc;u trả lời cho những c&acirc;u hỏi bất ngờ của kh&aacute;ch h&agrave;ng. Bạn n&ecirc;n lưu &yacute; thời gian gọi, tr&aacute;nh cảm gi&aacute;c l&agrave;m phiền cho kh&aacute;ch h&agrave;ng v&agrave; hạn chế gọi trong thời gian ăn cơm hay thời gian nghỉ ngơi.</p>\n<p dir=\"ltr\">Ch&igrave;nh v&igrave; vậy, kho&aacute; học&nbsp;<strong>B&iacute; quyết chốt đơn th&agrave;nh c&ocirc;ng 90% - Telesale, B&aacute;n h&agrave;ng online&nbsp;</strong>th&agrave;nh c&ocirc;ng 90% của Giảng vi&ecirc;n Trần Văn Tuấn - CEO Tasa sẽ gi&uacute;p bạn chốt được sale ngay trong cuộc gọi điện thoại hoặc chat với kh&aacute;ch h&agrave;ng khi b&aacute;n h&agrave;ng online. Đồng thời cung cấp cho bạn 4 bước x&acirc;y dựng kịch bản b&aacute;n h&agrave;ng v&agrave; 7 chiến lược chốt sale khiến kh&aacute;ch h&agrave;ng kh&ocirc;ng thể cưỡng lại được. Gi&uacute;p bạn, doanh thu c&ocirc;ng ty của bạn Tăng Vọt với tỷ lệ chốt sale l&ecirc;n 90%.</p>\n<p dir=\"ltr\">Bạn h&atilde;y nhanh tay đăng k&yacute; kh&oacute;a học &ldquo;<strong>B&iacute; quyết chốt đơn th&agrave;nh c&ocirc;ng 90% - Telesale, B&aacute;n h&agrave;ng online</strong>&rdquo; để được hưởng nhiều ưu đ&atilde;i lớn!</p>\n</div>\n</div>\n</div>\n</div>');
INSERT INTO `product` (`id`, `catalog_id`, `name`, `content`, `price`, `image_link`, `trailer_link`, `author_id`, `sale`, `status`, `number_user`, `list_number`, `content_full`) VALUES
(51, 170, 'Làm phim hoạt hình 2D với MAYA', 'Hướng dẫn làm phim hoạt hình 2D tạo nên 1 tác phẩm tuyệt vời một cách chuyên nghiệp với Maya', 36.00, '[9rX]-sddefault.webp', '[qaS]-Tạo-hoạt-hình-bằng-2D-với-Maya---Khổng-Minh-Phương.mp4', 19, 4, 1, 0, '[]', '<h3>Bạn sẽ học được g&igrave;</h3>\n<div class=\"content\">\n<div class=\"row\">\n<div class=\"col-sm-6 form-group\">\n<div class=\"title\">Tự l&agrave;m phim hoạt h&igrave;nh 2D chuy&ecirc;n nghiệp với MAYA</div>\n</div>\n<div class=\"col-sm-6 form-group\">\n<div class=\"title\">Thao t&aacute;c với camera v&agrave; quản l&yacute; vật thể trong m&ocirc;i trường 3D</div>\n</div>\n<div class=\"col-sm-6 form-group\">\n<div class=\"title\">Thiết kế nh&acirc;n vật hoạt h&igrave;nh với Paint v&agrave; Photoshop&nbsp;</div>\n</div>\n<div class=\"col-sm-6 form-group\">\n<div class=\"title\">Gắn xương v&agrave; phụ kiện cho nh&acirc;n vật tạo hoạt động linh hoạt tự nhi&ecirc;n</div>\n</div>\n<div class=\"col-sm-6 form-group\">\n<div class=\"title\">Tạo bộ điều khiển, kết nối biểu cảm cho nh&acirc;n vật</div>\n</div>\n<div class=\"col-sm-6 form-group\">\n<div class=\"title\">Điều chỉnh phối hợp &aacute;nh s&aacute;ng, diễn hoạt nh&acirc;n vật theo bối cảnh</div>\n</div>\n<div class=\"col-sm-6 form-group\">\n<div class=\"title\">Cắt gh&eacute;p v&agrave; chỉnh sửa video ho&agrave;n thiện với Adobe Premiere</div>\n<div class=\"title\">\n<p><strong>C&oacute; phải bạn:&nbsp;&nbsp;</strong></p>\n<ul>\n<li>Đang l&agrave;m trong bộ phận dựng phim của doanh nghiệp, muốn tạo ra những bộ phim hay quảng c&aacute;o dưới định dạng 2D?</li>\n<li>Hay bạn rất th&iacute;ch những nh&acirc;n vật đ&aacute;ng y&ecirc;u trong c&aacute;c bộ phim hoạt h&igrave;nh nước ngo&agrave;i?&nbsp;</li>\n<li>Bạn c&oacute; &yacute; tưởng về 1 bộ phim của ri&ecirc;ng bạn nhưng chưa biết bắt đầu từ đ&acirc;u, những bước cơ bản n&agrave;o để bắt tay thực hiện 1 bộ phim hoạt h&igrave;nh 2D?</li>\n<li>Bạn đ&atilde; c&oacute; những c&aacute;i nh&igrave;n tổng quan nhất về lĩnh vực l&agrave;m phim hoạt h&igrave;nh 2D v&agrave; bạn muốn nghi&ecirc;m t&uacute;c&nbsp;theo đuổi học dựng phim?</li>\n</ul>\n<p>Bạn c&oacute; thể th&agrave;nh thạo c&aacute;c c&ocirc;ng cụ Paint, Photoshop, nhưng để c&oacute; đủ kỹ năng nền tảng cho việc x&acirc;y dựng h&igrave;nh ảnh 2D th&igrave; chưa đủ. Kh&ocirc;ng chỉ l&agrave; c&aacute;c bản vẽ đơn giản, bạn phải học về c&aacute;c thao t&aacute;c với camera, c&aacute;ch tạo bộ điều khiển, gắn xương v&agrave; h&agrave;ng loạt thao t&aacute;c cần thiết kh&aacute;c &nbsp;gi&uacute;p nh&acirc;n vật của m&igrave;nh trở n&ecirc;n ho&agrave;n thiện v&agrave; sinh động hơn.</p>\n<p>Đừng để mơ ước của bạn về 1 t&aacute;c phẩm tuyệt vời định dạng 2D bị bỏ lỡ! H&atilde;y để Chuy&ecirc;n gia Kh&ocirc;ng Minh Phương hướng dẫn bạn phương ph&aacute;p tạo ra một bộ phim như thế với một phần mềm phổ biến tr&ecirc;n thế giới l&agrave; MAYA.</p>\n<p>Tham gia kh&oacute;a học&nbsp;<strong>\"L&agrave;m phim hoạt h&igrave;nh 2D với MAYA\"</strong>&nbsp;ngay h&ocirc;m nay để trở th&agrave;nh t&aacute;c giả ch&iacute;nh cho c&acirc;u chuyện hoạt h&igrave;nh 2D của bạn!</p>\n<p><strong>Kh&oacute;a học c&oacute; g&igrave; d&agrave;nh cho bạn?</strong></p>\n<p>&nbsp; &nbsp; &nbsp;✔️ Hướng dẫn chi tiết nhất về MAYA - c&ocirc;ng cụ để hoạt họa v&agrave; x&acirc;y dựng n&ecirc;n phim hoạt h&igrave;nh 2D của bạn</p>\n<p>&nbsp; &nbsp; &nbsp;✔️ Chi tiết c&aacute;c c&aacute;ch sử dụng, c&aacute;c thao t&aacute;c l&agrave;m việc với to&agrave;n bộ t&iacute;nh năng trong MAYA</p>\n<p>&nbsp; &nbsp; &nbsp;✔️ Hướng dẫn tạo h&igrave;nh nh&acirc;n vật hoạt h&igrave;nh từ Photoshop, c&aacute;ch vẽ nh&acirc;n vật trong Photoshop chi tiết v&agrave; đổ file từ Photoshop sang MAYA.</p>\n<p>&nbsp; &nbsp; &nbsp;✔️ C&aacute;ch gắn xương - một trong những bước quan trọng để gi&uacute;p nh&acirc;n vật 2D của bạn chuyển động</p>\n<p>&nbsp; &nbsp; &nbsp;✔️ Chi tiết c&aacute;c bước diễn họa - hoạt họa trong MAYA</p>\n<p>&nbsp; &nbsp; &nbsp;✔️ Kiến thức về &aacute;nh s&aacute;ng trong phim hoạt h&igrave;nh 2D</p>\n<p>&nbsp; &nbsp; &nbsp;✔️ C&aacute;ch chỉnh sửa v&agrave; xuất bản phim hoạt h&igrave;nh 2D của bạn bằng Adobe Premiere</p>\n<p>&nbsp; &nbsp; &nbsp;✔️ ...</p>\n<p>C&oacute; qu&aacute; nhiều kiến thức hay chỉ trong một kh&oacute;a học<strong>&nbsp;\"L&agrave;m phim hoạt h&igrave;nh 2D với MAYA\"!</strong>&nbsp;C&ograve;n chờ đợi g&igrave; nữa, tham gia kh&oacute;a học ngay h&ocirc;m nay n&agrave;o!</p>\n</div>\n</div>\n</div>\n</div>'),
(52, 101, 'Kỹ thuật viết PR - Quảng cáo - Bán hàng đúng tâm lý khách hàng', 'Tuyệt chiêu viết bài PR - Quảng cáo hiệu quả nhất mang đến bạn một tư duy đúng đắn cho người viết nội dung marketing.', 31.00, '[EM9]-maxresdefault.webp', '[ZhM]-Kỹ-thuật-viết-PR-Quảng-Cáo-Bán-Hàng-Đúng-Tâm-Lý-Khách-Hàng---Trần-Thành-Thiên.mp4', 19, 0, 1, 0, '[]', '<h3>Bạn sẽ học được g&igrave;</h3>\n<div class=\"content\">\n<div class=\"row\">\n<div class=\"col-sm-6 form-group\">\n<div class=\"title\">Viết h&agrave;ng loạt những ti&ecirc;u đề hiệu quả v&agrave; thu h&uacute;t đ&uacute;ng nh&oacute;m đối tượng kh&aacute;ch h&agrave;ng mục ti&ecirc;u chỉ trong 30 gi&acirc;y</div>\n</div>\n<div class=\"col-sm-6 form-group\">\n<div class=\"title\">Sở hữu tư duy của một copywriter nhiều năm kinh nghiệm chinh chiến đủ mọi ng&agrave;nh nghề&nbsp;</div>\n</div>\n<div class=\"col-sm-6 form-group\">\n<div class=\"title\">Tự tin viết bất kỳ sản phẩm/dịch vụ n&agrave;ođang kinh doanh v&igrave; những phương thức thực tế trong kho&aacute; học</div>\n</div>\n<div class=\"col-sm-6 form-group\">\n<div class=\"title\">Mở đầu c&aacute;c b&agrave;i viết k&iacute;ch th&iacute;ch kh&aacute;ch h&agrave;ng chịu d&agrave;nh thời gian để đọc b&agrave;i quảng c&aacute;o của bạn</div>\n</div>\n<div class=\"col-sm-6 form-group\">\n<div class=\"title\">Tr&igrave;nh b&agrave;y từ A - Z to&agrave;n bộ b&agrave;i viết l&ocirc;i cuốn kh&aacute;ch h&agrave;ng đọc hết b&agrave;i của bạn một c&aacute;ch tự nhi&ecirc;n nhất</div>\n</div>\n<div class=\"col-sm-6 form-group\">\n<div class=\"title\">N&acirc;ng cao khả năng viết content thu h&uacute;t để đạt c&aacute;c vị tr&iacute; cao: Trưởng nh&oacute;m/ Trưởng ph&ograve;ng Nội</div>\n<div class=\"title\">\n<h3 dir=\"ltr\"><strong>Nghệ thuật viết PR quảng c&aacute;o đ&aacute;nh v&agrave;o t&acirc;m l&yacute; kh&aacute;ch h&agrave;ng</strong></h3>\n<p>Hầu hết mọi người đều muốn viết những b&agrave;i viết c&oacute; thể đạt hiệu quả b&aacute;n h&agrave;ng nhưng kh&ocirc;ng biết bắt đầu từ đ&acirc;u, kh&ocirc;ng biết điểm b&aacute;n h&agrave;ng của b&agrave;i viết nằm chỗ n&agrave;o để đ&aacute;nh gi&aacute; hiệu quả, nhiều người đọc nhiều s&aacute;ch v&agrave; thử viết nhiều lần nhưng vẫn kh&ocirc;ng b&aacute;n được h&agrave;ng. Bởi v&igrave; mọi người thiếu 2 thứ cực kỳ quan trọng: Đ&oacute; l&agrave; một tư duy đ&uacute;ng v&agrave; những phương ph&aacute;p đ&uacute;ng</p>\n<p dir=\"ltr\">Ng&agrave;y nay, viết b&agrave;i PR sản phẩm kh&ocirc;ng c&ograve;n l&agrave; một c&ocirc;ng việc xa lạ với mọi người, doanh nghiệp kinh doanh. Với sự ph&aacute;t triển của internet th&igrave; viết b&agrave;i PR quảng c&aacute;o đ&aacute;nh v&agrave;o t&acirc;m l&yacute; kh&aacute;ch h&agrave;ng được xem l&agrave; một chiến lược b&aacute;n hiệu quả.</p>\n<p dir=\"ltr\">Những b&agrave;i viết n&agrave;y thường được đăng tải l&ecirc;n c&aacute;c trang website, fanpage, diễn đ&agrave;n hoặc th&ocirc;ng qua th&ocirc;ng c&aacute;o b&aacute;o ch&iacute;, tạp ch&iacute; kh&aacute;c nhau để đạt hiệu quả quảng c&aacute;o v&agrave; tiếp cận c&ocirc;ng ch&uacute;ng hiệu quả.</p>\n<p dir=\"ltr\">Những b&agrave;i viết PR n&agrave;y thường phải giới thiệu sản phẩm một c&aacute;ch g&acirc;y t&ograve; m&ograve;, tạo sự k&iacute;ch th&iacute;ch v&agrave; thu h&uacute;t người đọc nhất. Nghệ thuật viết b&agrave;i PR l&agrave; phải khiến cho người đọc muốn mua sản phẩm của bạn khi vừa được giới thiệu.</p>\n<p dir=\"ltr\">Tuy nhi&ecirc;n, để l&agrave;m được điều n&agrave;y th&igrave; bạn cần phải c&oacute; thời gian v&agrave; kiến thức. H&atilde;y tham khảo kh&oacute;a học &ldquo;<strong>Kỹ thuật viết PR - Quảng c&aacute;o - B&aacute;n h&agrave;ng đ&uacute;ng t&acirc;m l&yacute; kh&aacute;ch h&agrave;ng</strong>&rdquo; của giảng vi&ecirc;n&nbsp;<a href=\"https://unica.vn/teacher/thien-tran\">Thi&ecirc;n Trần</a>&nbsp;tr&ecirc;n UNICA để&nbsp; nắm được c&aacute;c tuyệt chi&ecirc;u viết b&agrave;i PR quảng c&aacute;o chuy&ecirc;n nghiệp.</p>\n<h3 dir=\"ltr\"><strong>Nội dung kh&oacute;a học</strong></h3>\n<p dir=\"ltr\">Kh&oacute;a học bao gồm 28 b&agrave;i giảng v&agrave; thời lượng học l&agrave; 01 giờ 59 ph&uacute;t, chia l&agrave;m 5 phần nội dung học chi tiết.</p>\n<p dir=\"ltr\">Kh&oacute;a học n&agrave;y mang đến bạn một tư duy đ&uacute;ng đắn c&aacute;ch học Content Marketing&nbsp;hiệu quả&nbsp;của người viết nội dung marketing v&agrave; h&agrave;ng loạt c&aacute;c phương thức để tự viết được một b&agrave;i viết PR - Quảng C&aacute;o - B&aacute;n H&agrave;ng đạt hiệu quả cao nhất.&nbsp;</p>\n<p dir=\"ltr\">H&atilde;y đến với chuy&ecirc;n gia đ&agrave;o tạo copywriter Thi&ecirc;n Trần, đến với kh&oacute;a học&nbsp;<strong>\"Kỹ thuật viết PR Quảng C&aacute;o B&aacute;n H&agrave;ng - Đ&uacute;ng T&acirc;m L&yacute; Kh&aacute;ch H&agrave;ng</strong>&nbsp;\" bạn sẽ nhận được rất nhiều lợi &iacute;ch đấy nh&eacute;!</p>\n</div>\n</div>\n</div>\n</div>'),
(53, 152, 'Yoga cho thanh xuân còn mãi', 'Cải thiện các chứng bệnh thường gặp của tuổi trung niên, lấy lại vóc dáng eo thon, giảm mệt mỏi, lo lắng, có được làn da tươi sáng, kéo dài tuổi xuân với Yoga.', 21.00, '[Iqo]-maxresdefault-(1).webp', '[tkS]-Yoga-cho-thanh-xuân-còn-mãi---Hà-Kim-Anh.mp4', 19, 2, 1, 0, '[]', '<div class=\"col-sm-6 form-group\">\n<div class=\"title\">Giảm c&acirc;n tự nhi&ecirc;n, loại bỏ mỡ thừa, c&oacute; được v&oacute;c d&aacute;ng thon gọn, từ bỏ những th&oacute;i quen xấu dẫn đến bệnh xương khớp</div>\n</div>\n<div class=\"col-sm-6 form-group\">\n<div class=\"title\">Sắc tố da sẽ được cải thiện r&otilde; rệt, kh&iacute; huyết lưu th&ocirc;ng, tăng cường oxy t&aacute;i tạo tế b&agrave;o, c&oacute; được l&agrave;n da hồng h&agrave;o đầy sức sống</div>\n</div>\n<div class=\"col-sm-6 form-group\">\n<div class=\"title\">Tăng sức đề kh&aacute;ng, đẩy l&ugrave;i những bệnh thường gặp ở lứa tuổi trung ni&ecirc;n</div>\n</div>\n<div class=\"col-sm-6 form-group\">\n<div class=\"title\">Tạo lập th&oacute;i quen mới, gi&uacute;p t&acirc;m th&aacute;i lu&ocirc;n an nhi&ecirc;n, vui vẻ, lạc quan.</div>\n</div>\n<div class=\"col-sm-6 form-group\">\n<div class=\"title\">K&eacute;o d&agrave;i tuổi&nbsp;xu&acirc;n nhờ Yoga</div>\n<div class=\"title\">\n<p dir=\"ltr\"><em><strong>Vấn đề của kh&aacute;ch h&agrave;ng</strong></em></p>\n<p dir=\"ltr\">❌ Bạn đang bị thừa c&acirc;n d&ugrave; đ&atilde; t&igrave;m đến nhiều phương ph&aacute;p như uống tr&agrave; giảm c&acirc;n, h&uacute;t mỡ, phẫu thuật...</p>\n<p dir=\"ltr\">❌ Bạn c&oacute; l&agrave;n da kh&ocirc;ng được tươi s&aacute;ng d&ugrave; đ&atilde; đến c&aacute;c spa l&agrave;m đẹp hoặc uống c&aacute;c loại thực phẩm chức năng nhập ngoại nổi tiếng.</p>\n<p dir=\"ltr\">❌ Bạn mất ngủ, hay lo &acirc;u mệt mỏi, c&oacute; khi đau to&agrave;n th&acirc;n m&agrave; kh&ocirc;ng biết nguy&ecirc;n nh&acirc;n tại sao??</p>\n<p dir=\"ltr\">❌ Đặc biệt khi thời tiết thay đổi, hiện tượng về tai mũi họng hoặc đau đầu ch&oacute;ng mặt l&agrave;m bạn cảm thấy v&ocirc; c&ugrave;ng kh&oacute; chịu.</p>\n<p dir=\"ltr\"><em><strong>Nhưng bạn đừng qu&aacute; lo lắng,&nbsp;</strong></em></p>\n<p dir=\"ltr\">❗ Từ nhiều thế kỷ nay, Yoga đ&atilde; phổ biến ở nhiều nước tr&ecirc;n Thế giới v&agrave; trở th&agrave;nh một bộ m&ocirc;n thể dục duy nhất r&egrave;n luyện thể chất v&agrave; tinh thần cho mọi lứa tuổi. C&oacute; những nơi Yoga như một t&ocirc;n gi&aacute;o huyền thoại gi&uacute;p con người sống khỏe mạnh hạnh ph&uacute;c v&agrave; b&igrave;nh an.</p>\n<p dir=\"ltr\">❗ Kh&oacute;a học \"<em><strong>Yoga cho thanh xu&acirc;n c&ograve;n m&atilde;i\"</strong></em>&nbsp;được bi&ecirc;n soạn c&ocirc;ng phu dựa tr&ecirc;n nền tảng về giải phẫu cơ thể con người ở lứa tuổi trung ni&ecirc;n, d&agrave;nh cho c&aacute;c bạn đang gặp phải c&aacute;c vấn đề tr&ecirc;n, đặc biệt l&agrave; người trung ni&ecirc;n v&agrave; phụ nữ sau sinh.&nbsp;</p>\n<p dir=\"ltr\"><em><strong>Nội dung kh&oacute;a học</strong></em></p>\n<p dir=\"ltr\">Gi&aacute;o tr&igrave;nh c&oacute; 27 b&agrave;i giảng chia l&agrave;m 6 học phần</p>\n<p dir=\"ltr\">- Phần 1: giới thiệu tổng quan</p>\n<p dir=\"ltr\">- Phần 2: Ổn định th&acirc;n t&acirc;m</p>\n<p dir=\"ltr\">- Phần 3: Khởi động</p>\n<p dir=\"ltr\">- Phần 4: C&aacute;c b&agrave;i tập phục hồi v&agrave; tăng cường sinh lực</p>\n<p dir=\"ltr\">- Phần 5: C&aacute;c b&agrave;i tập thư gi&atilde;n</p>\n<p dir=\"ltr\">- Phần 6: Tổng kết kh&oacute;a học</p>\n<p dir=\"ltr\"><em><strong>Lợi &iacute;ch kh&oacute;a học</strong></em></p>\n<p dir=\"ltr\">✔️ Th&ocirc;ng qua kh&oacute;a học,&nbsp;<em><strong>Huấn luyện vi&ecirc;n Yoga H&agrave; Kim Anh</strong></em>&nbsp;sẽ hướng dẫn bạn phương ph&aacute;p thở đ&uacute;ng, c&ugrave;ng một số b&agrave;i thở đặc biệt gi&uacute;p cung cấp lượng oxy dồi d&agrave;o cho cơ thể m&agrave; bạn chưa từng được cảm nhận.&nbsp;</p>\n<p dir=\"ltr\">✔️ C&aacute;c b&agrave;i tập hỗ trợ điều trị những bệnh thường gặp như vi&ecirc;m mũi dị ứng, đau đầu ch&oacute;ng mặt khi thời tiết thay đổi, bệnh đau mỏi vai g&aacute;y, huyết &aacute;p tiểu đường... Từ đ&oacute; cải thiện sức khỏe của bạn, gi&uacute;p bạn c&oacute; được tinh thần lu&ocirc;n an vui hạnh ph&uacute;c.</p>\n</div>\n</div>'),
(54, 178, 'Mong', '1234', 55.00, '[QdM]-3.jpg', '[gAH]-Islands---2119.mp4', 19, 50, 1, 0, '[]', '<p>Mongv vsvdsv</p>');

